INSERT INTO tourism_tourismplace (city_id, name, description, image_url) VALUES
(1, 'Red Fort', 'UNESCO World Heritage Site built in the 17th century by Shah Jahan. This massive red sandstone fort is known for its stunning architecture and historical significance.', '/media/place_images/red_fort.jpg'),
(1, 'India Gate', 'A war memorial dedicated to soldiers who died in World War I. This iconic archway stands at the center of New Delhi and is surrounded by lush lawns.', '/media/place_images/india_gate.jpg'),
(1, 'Qutub Minar', 'UNESCO World Heritage Site featuring the world''s tallest brick minaret. This 73-meter tall tower was built in 1193 and is surrounded by ancient ruins.', '/media/place_images/qutub_minar.jpg'),
(1, 'Humayun''s Tomb', 'UNESCO World Heritage Site and the first garden-tomb on the Indian subcontinent. This magnificent structure inspired the Taj Mahal.', '/media/place_images/humayuns_tomb.jpg'),
(1, 'Lotus Temple', 'A Bahá''í House of Worship notable for its flowerlike shape. The temple is made of pure white marble and is surrounded by nine pools and gardens.', '/media/place_images/lotus_temple.jpg'),
(1, 'Akshardham Temple', 'A Hindu temple complex showcasing millennia of traditional Hindu and Indian culture. Features intricate carvings, a musical fountain, and exhibitions.', '/media/place_images/akshardham_temple.jpg'),
(1, 'Jama Masjid', 'India''s largest mosque, built by Emperor Shah Jahan. Can accommodate up to 25,000 worshippers and features three gates, four towers, and two minarets.', '/media/place_images/jama_masjid.jpg'),
(1, 'Chandni Chowk', 'One of the oldest and busiest markets in Old Delhi. Known for its narrow winding lanes full of shops selling spices, textiles, jewelry, and street food.', '/media/place_images/chandni_chowk.jpg'),
(1, 'Rashtrapati Bhavan', 'The official residence of the President of India. This massive building has 340 rooms spread over 4 floors and is surrounded by the Mughal Gardens.', '/media/place_images/rashtrapati_bhavan.jpg'),
(1, 'National Museum', 'Houses over 200,000 artworks spanning more than 5,000 years of Indian cultural heritage. Features artifacts from the Indus Valley Civilization to modern times.', '/media/place_images/national_museum.jpg'),
(1, 'Jantar Mantar', 'An 18th-century astronomical observatory built by Maharaja Jai Singh II. Features masonry instruments used to predict eclipses and track stars.', '/media/place_images/jantar_mantar.jpg'),
(1, 'Lodhi Gardens', 'A historical park containing architectural works of the 15th-century Lodhi dynasty. Spans 90 acres and features tombs, mosques, and gardens.', '/media/place_images/lodhi_gardens.jpg'),
(1, 'Gurudwara Bangla Sahib', 'One of the most prominent Sikh houses of worship in Delhi. Known for its association with the eighth Sikh Guru, Guru Har Krishan.', '/media/place_images/gurudwara_bangla_sahib.jpg'),
(1, 'Hauz Khas Complex', 'A historic complex dating back to the 13th century. Features a water tank, Islamic seminary, mosque, pavilions, and a royal tomb.', '/media/place_images/hauz_khas_complex.jpg'),
(1, 'National Rail Museum', 'Showcases the rail heritage of India. Exhibits include vintage locomotives, coaches, saloons, and artifacts from the 1800s onward.', '/media/place_images/national_rail_museum.jpg'),
(1, 'Parliament House', 'A circular colonnaded building that houses the Parliament of India. An impressive example of British colonial architecture.', '/media/place_images/parliament_house.jpg'),
(1, 'Connaught Place', 'One of the largest financial, commercial, and business centers in New Delhi. Features a circular design with Georgian-style architecture.', '/media/place_images/connaught_place.jpg'),
(1, 'Gandhi Smriti', 'The location where Mahatma Gandhi spent the last 144 days of his life and was assassinated. Now a museum dedicated to his life and principles.', '/media/place_images/gandhi_smriti.jpg'),
(1, 'Purana Qila', 'An ancient fortress believed to be built on the site of Indraprastha, the legendary city from the Mahabharata. Features massive walls and three arched gateways.', '/media/place_images/purana_qila.jpg'),
(1, 'National Zoological Park', 'A 176-acre zoo home to over 1,300 animals representing around 130 species. Features a 16th-century citadel and spacious enclosures.', '/media/place_images/national_zoological_park.jpg');
