INSERT INTO tourism_tourismplace (city_id, name, description, image_url) VALUES
(2, 'Gateway of India', 'An iconic arch monument built during the 20th century overlooking the Arabian Sea. This monument was erected to commemorate the landing of King George V and Queen Mary in 1911.', '/media/place_images/gateway_of_india.jpg'),
(2, 'Marine Drive', 'A 3.6-kilometer-long boulevard in South Mumbai that curves along the coastline. Also known as the Queen''s Necklace because the street lights resemble a string of pearls when viewed from above at night.', '/media/place_images/marine_drive.jpg'),
(2, 'Elephanta Caves', 'UNESCO World Heritage Site located on Elephanta Island in Mumbai Harbour. The island features ancient cave temples carved out of rock and dedicated to Lord Shiva.', '/media/place_images/elephanta_caves.jpg'),
(2, 'Chhatrapati Shivaji Terminus', 'A historic railway station and UNESCO World Heritage Site known for its Victorian Gothic Revival architecture. One of the busiest railway stations in India.', '/media/place_images/chhatrapati_shivaji_terminus.jpg'),
(2, 'Juhu Beach', 'One of the most famous beaches in Mumbai, known for its food stalls, celebrities'' homes, and scenic views of the Arabian Sea. A popular recreational spot.', '/media/place_images/juhu_beach.jpg'),
(2, 'Siddhivinayak Temple', 'A Hindu temple dedicated to Lord Ganesha. One of the richest temples in Mumbai and visited by people of all faiths.', '/media/place_images/siddhivinayak_temple.jpg'),
(2, 'Colaba Causeway', 'A commercial street and a major causeway in Colaba. Known for its shopping, restaurants, and famous landmarks including Leopold Cafe and Taj Mahal Palace Hotel.', '/media/place_images/colaba_causeway.jpg'),
(2, 'Haji Ali Dargah', 'A mosque and dargah (tomb) located on an islet off the coast of Worli. Contains the tomb of Sayed Peer Haji Ali Shah Bukhari.', '/media/place_images/haji_ali_dargah.jpg'),
(2, 'Film City', 'An integrated film studio complex with several recording rooms, gardens, lakes, theaters, and grounds. The hub of the Bollywood film industry.', '/media/place_images/film_city.jpg'),
(2, 'Bandra-Worli Sea Link', 'An 8-lane cable-stayed bridge that links Bandra in the Western Suburbs to Worli in South Mumbai. One of the most recognizable landmarks of Mumbai.', '/media/place_images/bandra_worli_sea_link.jpg'),
(2, 'Sanjay Gandhi National Park', 'A large protected area in the northern part of Mumbai city. Known for its biodiversity, ancient Kanheri caves, and a small population of leopards.', '/media/place_images/sanjay_gandhi_national_park.jpg'),
(2, 'Chowpatty Beach', 'A public beach along the Marine Drive. Famous for its Ganesh Chaturthi celebrations and a variety of street foods.', '/media/place_images/chowpatty_beach.jpg'),
(2, 'Mahalaxmi Temple', 'One of the most famous temples of Mumbai dedicated to Mahalakshmi, the goddess of wealth and wisdom. Located on Bhulabhai Desai Road.', '/media/place_images/mahalaxmi_temple.jpg'),
(2, 'Nehru Science Centre', 'India''s largest interactive science center located in Worli. Features over 500 hands-on and interactive science exhibits.', '/media/place_images/nehru_science_centre.jpg'),
(2, 'Hanging Gardens', 'Also known as Pherozeshah Mehta Gardens, these terraced gardens provide panoramic views of the Arabian Sea. Located at the top of Malabar Hill.', '/media/place_images/hanging_gardens.jpg'),
(2, 'Kanheri Caves', 'Ancient Buddhist caves located within the Sanjay Gandhi National Park. The caves, dating from the 1st century BCE to the 10th century CE, served as Buddhist monasteries.', '/media/place_images/kanheri_caves.jpg'),
(2, 'Jehangir Art Gallery', 'An art gallery located in South Mumbai. Built in 1952, it is one of the most prestigious art galleries in India.', '/media/place_images/jehangir_art_gallery.jpg'),
(2, 'Taraporewala Aquarium', 'India''s oldest aquarium located on Marine Drive. Houses marine and freshwater fishes.', '/media/place_images/taraporewala_aquarium.jpg'),
(2, 'Royal Opera House', 'The only surviving opera house in India, located on Charni Road. Recently restored to its former glory, it now hosts performances and cultural events.', '/media/place_images/royal_opera_house.jpg'),
(2, 'Mani Bhavan', 'A museum and historical building dedicated to Gandhi, where he stayed during his visits to Mumbai between 1917 and 1934.', '/media/place_images/mani_bhavan.jpg');
