INSERT INTO tourism_tourismplace (city_id, name, description, image_url) VALUES
(3, 'Lalbagh Botanical Garden', 'A 240-acre garden designed in the style of Mughal Gardens. Home to India''s largest collection of tropical plants, a glass house, and a lake.', '/media/place_images/lalbagh_botanical_garden.jpg'),
(3, 'Bangalore Palace', 'Built in 1887, this palace was inspired by England''s Windsor Castle. Features Tudor-style architecture with fortified towers, arches, and green lawns.', '/media/place_images/bangalore_palace.jpg'),
(3, 'Cubbon Park', 'A landmark 300-acre lung space of Bangalore, home to diverse species of plants and trees. Features several neo-classical styled government buildings.', '/media/place_images/cubbon_park.jpg'),
(3, 'Vidhana Soudha', 'The seat of Karnataka''s state legislature, this imposing granite building is one of the best examples of Neo-Dravidian architecture and incorporates elements of Indo-Saracenic and Dravidian styles.', '/media/place_images/vidhana_soudha.jpg'),
(3, 'ISKCON Temple Bangalore', 'One of the largest ISKCON temples in the world, known for its blend of modern technology and spiritual tradition. The temple is built in a unique blend of contemporary and traditional styles.', '/media/place_images/iskcon_temple_bangalore.jpg'),
(3, 'Bannerghatta National Park', 'A biological reserve that also contains a zoo, a pet corner, an animal rescue center, a butterfly enclosure, an aquarium, a snake house, and a safari park.', '/media/place_images/bannerghatta_national_park.jpg'),
(3, 'Tipu Sultan''s Summer Palace', 'An example of Indo-Islamic architecture, this palace was the summer residence of Tipu Sultan. It is entirely made of teak wood with finely embellished arches, pillars, and balconies.', '/media/place_images/tipu_sultan_summer_palace.jpg'),
(3, 'Wonderla Amusement Park', 'One of the largest amusement parks in India, featuring water rides, land rides, and a musical fountain. A perfect place for family entertainment.', '/media/place_images/wonderla_amusement_park.jpg'),
(3, 'Ulsoor Lake', 'One of the largest lakes in Bangalore, spread over an area of 125 acres. Popular for boating and features a walking path around its perimeter.', '/media/place_images/ulsoor_lake.jpg'),
(3, 'Nandi Hills', 'An ancient hill fortress located about 60 km from Bangalore. Known for its scenic beauty, sunrise views, and historical monuments.', '/media/place_images/nandi_hills.jpg'),
(3, 'National Gallery of Modern Art', 'Housed in a colonial mansion, this gallery showcases modern and contemporary art from India and around the world.', '/media/place_images/national_gallery_modern_art.jpg'),
(3, 'HAL Aerospace Museum', 'India''s first aerospace museum, showcasing the history of HAL and India''s aviation industry. Features aircraft models, flight simulators, and engines.', '/media/place_images/hal_aerospace_museum.jpg'),
(3, 'Bull Temple', 'A 16th-century temple dedicated to Nandi Bull (the mount of Lord Shiva). Features a large monolithic statue of Nandi.', '/media/place_images/bull_temple.jpg'),
(3, 'Innovative Film City', 'An entertainment theme park featuring replicas of famous monuments, a wax museum, a dinosaur world, and various other attractions.', '/media/place_images/innovative_film_city.jpg'),
(3, 'Karnataka Chitrakala Parishath', 'An art complex featuring multiple galleries that showcase paintings, sculptures, and folk art from across India.', '/media/place_images/karnataka_chitrakala_parishath.jpg'),
(3, 'Freedom Park', 'A former central jail that has been converted into a recreational area with museums, exhibition spaces, and a meditation center.', '/media/place_images/freedom_park.jpg'),
(3, 'Jawaharlal Nehru Planetarium', 'A planetarium that offers various shows about astronomy and space science. Also houses an interactive science park.', '/media/place_images/jawaharlal_nehru_planetarium.jpg'),
(3, 'St. Mary''s Basilica', 'The oldest church in Bangalore, known for its Gothic-style architecture. Famous for the annual St. Mary''s Feast.', '/media/place_images/st_marys_basilica.jpg'),
(3, 'Commercial Street', 'A major shopping area in the Central Business District of Bangalore. Known for its mix of branded showrooms and street-side shops.', '/media/place_images/commercial_street.jpg');
