INSERT INTO tourism_tourismplace (city_id, name, description, image_url) VALUES
(4, 'Marina Beach', 'The second-longest urban beach in the world stretching 13 km along the Bay of Bengal. Features a lighthouse, statues, and is a popular spot for sunrise views.', '/media/place_images/marina_beach.jpg'),
(4, 'Kapaleeshwarar Temple', 'A 7th-century Dravidian-style temple dedicated to Lord Shiva. Known for its ornate gopuram (tower) and is an important pilgrimage site.', '/media/place_images/kapaleeshwarar_temple.jpg'),
(4, 'Fort St. George', 'Built in 1644 by the British East India Company, it was the first English fortress in India. Houses St. Mary''s Church and a museum with colonial artifacts.', '/media/place_images/fort_st_george.jpg'),
(4, 'Government Museum', 'One of the oldest museums in India, established in 1851. Features a diverse collection including archaeology, numismatics, zoology, and natural history.', '/media/place_images/government_museum.jpg'),
(4, 'Santhome Cathedral Basilica', 'Built in the 16th century by Portuguese explorers, this Gothic-style cathedral is believed to house the tomb of St. Thomas the Apostle.', '/media/place_images/santhome_cathedral.jpg'),
(4, 'Elliot''s Beach', 'Also known as Besant Nagar Beach, this quiet and clean beach is less crowded than Marina Beach. Features the Karl Schmidt Memorial and water sports facilities.', '/media/place_images/elliots_beach.jpg'),
(4, 'Guindy National Park', 'One of the smallest national parks in India, located in the heart of Chennai. Home to various species of deer, jackals, and over 100 species of birds.', '/media/place_images/guindy_national_park.jpg'),
(4, 'Valluvar Kottam', 'A monument dedicated to the Tamil poet and philosopher Thiruvalluvar. Built in the shape of a temple chariot, it features all 1,330 verses of his work, Thirukkural.', '/media/place_images/valluvar_kottam.jpg'),
(4, 'Cholamandal Artists'' Village', 'India''s largest artists'' commune, established in 1966. Features galleries, open-air theaters, and artists'' studios.', '/media/place_images/cholamandal_artists_village.jpg'),
(4, 'Birla Planetarium', 'One of the oldest planetariums in India, offering shows on astronomy and space science. Also houses an astronomy museum.', '/media/place_images/birla_planetarium.jpg'),
(4, 'DakshinaChitra', 'A living museum of art, architecture, lifestyle, and performing arts of South India. Features traditional houses from various South Indian states.', '/media/place_images/dakshinachitra.jpg'),
(4, 'MGR Film City', 'A film studio and amusement park named after the former Tamil Nadu Chief Minister and actor, M.G. Ramachandran. Features shooting locations and entertainment options.', '/media/place_images/mgr_film_city.jpg'),
(4, 'VGP Universal Kingdom', 'An amusement and water park with various rides, attractions, and entertainment options. Located near the beach.', '/media/place_images/vgp_universal_kingdom.jpg'),
(4, 'Thousand Lights Mosque', 'A prominent mosque built in the early 19th century. Named for the 1,000 oil lamps that supposedly light its main hall.', '/media/place_images/thousand_lights_mosque.jpg'),
(4, 'Arignar Anna Zoological Park', 'Also known as Vandalur Zoo, this 1,300-acre facility is one of the largest zoos in South Asia. Home to over 170 species of mammals, birds, and reptiles.', '/media/place_images/arignar_anna_zoological_park.jpg'),
(4, 'Parthasarathy Temple', 'One of the oldest temples in Chennai dedicated to Lord Krishna. Built during the 8th century by the Pallavas.', '/media/place_images/parthasarathy_temple.jpg'),
(4, 'Chennai Rail Museum', 'Showcases the heritage of the Southern Railway. Features vintage locomotives, coaches, and exhibits on the history of Indian Railways.', '/media/place_images/chennai_rail_museum.jpg'),
(4, 'Theosophical Society', 'Headquartered in Chennai, this spiritual organization has a beautiful garden campus with a 450-year-old banyan tree and a library.', '/media/place_images/theosophical_society.jpg'),
(4, 'Vivekananda House', 'Also known as Ice House, this is where Swami Vivekananda stayed during his visit to Chennai. Now a museum dedicated to his life and teachings.', '/media/place_images/vivekananda_house.jpg'),
(4, 'Tiger Cave', 'An 8th-century rock-cut Hindu temple complex located near Mamallapuram. Named for the tiger-shaped carvings that adorn the entrance.', '/media/place_images/tiger_cave.jpg');
